import React, { useRef, useState, useEffect } from 'react';
import '../css/Intro.css';

function TimelineItem({ title, period, details, isActive, onClick }) {
  return (
    <div
      className={`timeline-item ${isActive ? 'active' : 'inactive'}`}
      onClick={onClick}
    >
      <div className="timeline-header">
        <h3>{title}</h3>
        <span className="period">{period}</span>
      </div>
      {isActive && (
        <ul className="timeline-details">
          {details.map((detail, i) => (
            <li key={i}>{detail}</li>
          ))}
        </ul>
      )}
    </div>
  );
}

function Intro() {
  const [activeIndex, setActiveIndex] = useState(0);
  const containerRef = useRef(null);

  const items = [
    // combine experienceItems and educationItems here or keep separate sections
    {
      title: 'Graduate Researcher - Stony Brook University',
      period: 'Jan 2023 – Present',
      details: [
        'Collected and anonymized clinical datasets...',
        'Designed a PostgreSQL-backed system...',
        'Built an LLM-powered query interface...',
      ],
    },
    {
      title: 'Software Engineer - LocoNav',
      period: 'Jan 2022 – Aug 2023',
      details: [
        'Built scalable backend services...',
        'Implemented a distributed task orchestration engine...',
      ],
    },
    // add education items below
  ];

  // Track scroll to find active element
  useEffect(() => {
    const container = containerRef.current;
    const items = container.querySelectorAll('.timeline-item');

    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting && entry.intersectionRatio > 0.6) {
            const index = [...items].indexOf(entry.target);
            setActiveIndex(index);
          }
        });
      },
      { root: container, threshold: [0.5, 0.75] }
    );

    items.forEach((item) => observer.observe(item));
    return () => observer.disconnect();
  }, []);

  return (
    <section id="intro">
      <div className="intro-container">
        <h2 className="section-title">{'<Experience & Education />'}</h2>
        <div className="timeline-box" ref={containerRef}>
          {items.map((item, index) => (
            <TimelineItem
              key={index}
              {...item}
              isActive={index === activeIndex}
              onClick={() => setActiveIndex(index)}
            />
          ))}
        </div>
      </div>
    </section>
  );
}

export default Intro;
