import React, { useEffect } from 'react';
import '../css/skills.css';

function Skills() {
  const skills = {
    "Programming Languages": [
      "Java", "Python", "C++", "JavaScript", "TypeScript", "SQL", "HTML/CSS", "Shell Scripting"
    ],
    "Frameworks & Libraries": [
      "Spring Boot", "Spring MVC", "Spring Security", "Django", "Flask", "React.js", "Node.js", "Nginx", "REST APIs", "GraphQL"
    ],
    "Cloud Platforms & DevOps": [
      "AWS (EC2, S3, Lambda, RDS, SNS, SQS, IAM)", "Azure", "Docker", "Jenkins", "Terraform", "Kubernetes"
    ],
    "Databases": [
      "MySQL", "PostgreSQL", "MongoDB", "DynamoDB", "Redis", "NoSQL"
    ],
    "Monitoring & Logging": [
      "Prometheus", "Grafana", "ELK Stack", "Elastic APM", "Datadog"
    ],
    "Messaging & Streaming": [
      "Apache Kafka", "RTMP", "HLS", "AWS SQS", "Celery"
    ]
  };

  // ðŸ‘‡ Add scroll animation using IntersectionObserver
  useEffect(() => {
    const tags = document.querySelectorAll('.skill-tag');
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('visible');
            observer.unobserve(entry.target); // animate once
          }
        });
      },
      { threshold: 0.2 }
    );
    tags.forEach((tag) => observer.observe(tag));
    return () => observer.disconnect();
  }, []);

  return (
    <section id="skills">
      <div className="skills-container">
        <h2 className="section-title">{'<Skills />'}</h2>
        <div className="skills-grid">
          {Object.entries(skills).map(([category, items]) => (
            <div key={category} className="skills-card">
              <h3 className="skills-category">{category}</h3>
              <div className="skills-list">
                {items.map((skill, i) => (
                  <span key={i} className="skill-tag">
                    {skill}
                  </span>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default Skills;
