import React, { useState, useEffect, useRef } from 'react';
import '../css/skills.css';

function Skills() {
  const skills = {
    "Programming Languages": [
      "Java", "Python", "C++", "JavaScript", "TypeScript", "SQL", "HTML/CSS", "Shell Scripting"
    ],
    "Frameworks & Libraries": [
      "Spring Boot", "Spring MVC", "Spring Security", "Django", "Flask", "React.js", "Node.js", "Nginx", "REST APIs", "GraphQL"
    ],
    "Cloud Platforms & DevOps": [
      "AWS (EC2, S3, Lambda, RDS, SNS, SQS, IAM)", "Azure", "Docker", "Jenkins", "Terraform", "Kubernetes"
    ],
    "Databases": [
      "MySQL", "PostgreSQL", "MongoDB", "DynamoDB", "Redis", "NoSQL"
    ],
    "Monitoring & Logging": [
      "Prometheus", "Grafana", "ELK Stack", "Elastic APM", "Datadog"
    ],
    "Messaging & Streaming": [
      "Apache Kafka", "RTMP", "HLS", "AWS SQS", "Celery"
    ]
  };

  const categories = Object.entries(skills);
  const [currentIndex, setCurrentIndex] = useState(0);
  const intervalRef = useRef(null);

  const handlePrev = () => {
    setCurrentIndex((prev) =>
      prev === 0 ? categories.length - 1 : prev - 1
    );
  };

  const handleNext = () => {
    setCurrentIndex((prev) =>
      prev === categories.length - 1 ? 0 : prev + 1
    );
  };

  // Auto-slide
  useEffect(() => {
    startAutoSlide();
    return () => stopAutoSlide();
  }, []);

  const startAutoSlide = () => {
    stopAutoSlide();
    intervalRef.current = setInterval(() => {
      setCurrentIndex((prev) =>
        prev === categories.length - 1 ? 0 : prev + 1
      );
    }, 5000); // 5s per slide
  };

  const stopAutoSlide = () => {
    if (intervalRef.current) clearInterval(intervalRef.current);
  };

  return (
    <section id="skills">
      <div
        className="skills-carousel-container"
        onMouseEnter={stopAutoSlide}
        onMouseLeave={startAutoSlide}
      >
        <h2 className="section-title">{'<Skills />'}</h2>

        <div className="carousel-track" style={{ transform: `translateX(-${currentIndex * 100}%)` }}>
          {categories.map(([categoryName, items], idx) => (
            <div
              key={idx}
              className={`skills-card ${idx === currentIndex ? 'active' : 'side'}`}
            >
              <h3 className="skills-category">{categoryName}</h3>
              <div className="skills-list scrollable">
                {items.map((skill, i) => (
                  <span key={i} className="skill-bubble">{skill}</span>
                ))}
              </div>
            </div>
          ))}
        </div>

        {/* Navigation */}
        <div className="nav-controls">
          <button className="nav-btn" onClick={handlePrev}>
            &#10094;
          </button>
          <button className="nav-btn" onClick={handleNext}>
            &#10095;
          </button>
        </div>
      </div>
    </section>
  );
}

export default Skills;
