import React, { useRef } from 'react';
import Navbar from './components/js/navbar';
import Home from './components/js/home';
import Projects from './components/js/projects';
import Contact from './components/js/contact';
import Footer from './components/js/footer';

function App() {
  // Create section references
  const homeRef = useRef(null);
  const projectsRef = useRef(null);
  const contactRef = useRef(null);

  // ✅ Ensure this function is correctly placed inside the App function
  const scrollToSection = (sectionRef) => {
    if (sectionRef && sectionRef.current) {
      sectionRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  };

  return (
    <div>
      {/* Pass scroll function & section refs to Navbar */}
      <Navbar scrollToSection={scrollToSection} refs={{ homeRef, projectsRef, contactRef }} />

      {/* Sections with refs */}
      <section ref={homeRef} id="home">
        <Home />
      </section>
      <section ref={projectsRef} id="projects">
        <Projects />
      </section>
      <section ref={contactRef} id="contact">
        <Contact />
      </section>
    </div>
  );
}

export default App;
