import React, { useState } from 'react';
import '../css/skills.css';

// ðŸ”¹ Icons you already have
import jsIcon from '../images/js.svg';
import pyIcon from '../images/python.svg';
import javaIcon from '../images/java.svg';
import sqlIcon from '../images/sql.svg';
import htmlIcon from '../images/html.svg';
import dockerIcon from '../images/docker.svg';
import jenkinsIcon from '../images/jenkins.svg';
import gitIcon from '../images/git.svg';
import reactIcon from '../images/react.svg';
import awsIcon from '../images/aws.svg';
import kubIcon from '../images/kubernetes.svg';
import redisIcon from '../images/redis.svg';
import djangoIcon from '../images/django.svg';
import springIcon from '../images/springboot.svg';
import kafkaIcon from '../images/kafka.svg';

// ðŸ”¹ Map skills â†’ icons
const skillIcons = {
  JavaScript: jsIcon,
  Python: pyIcon,
  Java: javaIcon,
  SQL: sqlIcon,
  'HTML/CSS': htmlIcon,
  React: reactIcon,
  AWS: awsIcon,
  Docker: dockerIcon,
  Jenkins: jenkinsIcon,
  Git: gitIcon,
  Kubernetes: kubIcon,
  Redis: redisIcon,
  Kafka: kafkaIcon,
  Django: djangoIcon,
  SpringBoot: springIcon,
};

function Skills() {
  const allSkills = Object.keys(skillIcons); // âœ… Only show skills that have icons

  const [hoveredSkill, setHoveredSkill] = useState(null);
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });

  const handleMouseMove = (e) => {
    setMousePos({ x: e.clientX, y: e.clientY });
  };

  return (
    <section id="skills">
      <div className="skills-container">
        <h2 className="section-title">{'<Skills />'}</h2>

        <div className="skills-bubble-grid">
          {allSkills.map((skill, idx) => (
            <div
              key={idx}
              className="skill-bubble"
              onMouseEnter={() => setHoveredSkill(skill)}
              onMouseLeave={() => setHoveredSkill(null)}
              onMouseMove={handleMouseMove}
            >
              <img src={skillIcons[skill]} alt={skill} className="skill-bubble-icon" />
            </div>
          ))}
        </div>

        {hoveredSkill && (
          <div
            className="skill-tooltip"
            style={{ top: mousePos.y + 20, left: mousePos.x + 20 }}
          >
            {hoveredSkill}
          </div>
        )}
      </div>
    </section>
  );
}

export default Skills;
